import { React, useEffect, useState } from 'react'

import { Content, Row, Col } from '../modules/Grid'
import { Card } from '../modules/Card'
import monsters from '../monstersdata.json'

const cardRender = monsters.map(
  (monster)=>
  <Col col='col-md-3' key={monster.id}>
    <Card 
      id={monster.id}
      key={monster.id}
      name={monster.name} 
      type={monster.type} 
      size={monster.size} 
      cr={monster.cr} 
      aligment={monster.aligment} 
      imgSrc={monster.imgSrc} 
    /> 
  </Col>
)

function Search(props){
  document.title = `Busca | Monster Compendium `;
  
  const searchTerm = JSON.parse(localStorage.getItem('busca'))
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
       const results = monsters.filter(monster =>
         monster.name.toLowerCase().includes(searchTerm)
      );
       setSearchResults(results);
    }, [searchResults]);
    
    console.log('results')
    console.log(results)
    console.log('searchResults')
    console.log(searchResults)
    console.log('searchTerm')
    console.log(searchTerm)

    return (
        
        <div>
          
          <div style={{height: 20}}></div>
    
          <Content container='container'>
            <Row>
    
              <p className='monster-name'>{searchTerm}</p>

    
            </Row>
          </Content>
        </div>
      );
}

export default Search ;
