import React, { useState } from 'react';
import { Content, Row, Col } from '../modules/Grid';
import monsters from '../monsters.js'


// const cardRender = monsters.map(
//     (monster)=>
//     <Col col='col-md-3'>
//       <Card 
//         id={monster.id}
//         key={monster.id}
//         name={monster.name} 
//         type={monster.type} 
//         size={monster.size} 
//         cr={monster.cr} 
//         aligment={monster.aligment} 
//         imgSrc={monster.imgSrc} 
//       /> 
//     </Col>
//   )

export default function Detail({match}) {
    const [ monster ] = useState(monsters[`${match.params.id - 1}`]);
    document.title = `${monster.name} | Monster Compendium `;

    return (
        <div>
            <div style={{height: 40}}></div>

            <Content container='container detail-bg'>

                <div className='p-5'>
                    <div>
                        <button type="button" class="btn btn-outline-info">Voltar</button>

                        <p className=' pt-3'>{monster.aligment}    .    {monster.type}</p>
                        <h1 className='detail-titles'>{monster.name}</h1>
                        
                    </div>

                    <Content container='container'>
                        <Row>
                            <Col col='col-md-6'>
                                <div className=''>
                                    <Row>
                                        <Col col='col p-0'>
                                            <div className='text-center attributes'>
                                                <p>STR</p>
                                                <h3 className='detail-titles'>{monster.D20.abilities.STR}</h3>
                                            </div>
                                        </Col>
                                        <Col col='col p-0'>
                                            <div className='text-center attributes'>
                                                <p>DEX</p>
                                                <h3 className='detail-titles'>{monster.D20.abilities.DEX}</h3>
                                            </div>
                                        </Col>
                                        <Col col='col p-0'>
                                            <div className='text-center attributes'>
                                                <p>CON</p>
                                                <h3 className='detail-titles'>{monster.D20.abilities.CON}</h3>
                                            </div>
                                        </Col>
                                        <Col col='col p-0'>
                                            <div className='text-center attributes'>
                                                <p>INT</p>
                                                <h3 className='detail-titles'>{monster.D20.abilities.INT}</h3>
                                            </div>
                                        </Col>
                                        <Col col='col p-0'>
                                            <div className='text-center attributes'>
                                                <p>WIS</p>
                                                <h3 className='detail-titles'>{monster.D20.abilities.WIS}</h3>
                                            </div>
                                        </Col>
                                        <Col col='col p-0'>
                                            <div className='text-center attributes'>
                                                <p>CHA</p>
                                                <h3 className='detail-titles'>{monster.D20.abilities.CHA}</h3>
                                            </div>
                                        </Col>
                                    </Row>
                                </div>
                            </Col>
                            <Col col='col-md-6'>
                                <h3 className='detail-titles'>Descrição</h3>
                                <p>{monster.desc}</p>
                            </Col>
                        </Row>  
                    </Content>
                </div>

            </Content>
        </div>
    )
}
