import React, { useState } from 'react';
import { Content, Row, Col } from '../modules/Grid';
import monsters from '../monsters.js'
import { useHistory } from "react-router-dom";
import texture from '../img/texture.jpg'

// const cardRender = monsters.map(
//     (monster)=>
//     <Col col='col-md-3'>
//       <Card 
//         id={monster.id}
//         key={monster.id}
//         name={monster.name} 
//         type={monster.type} 
//         size={monster.size} 
//         cr={monster.cr} 
//         aligment={monster.aligment} 
//         imgSrc={monster.imgSrc} 
//       /> 
//     </Col>
//   )


export const BackButton = () => {
    let history = useHistory();
    return (
        <>
          <button className='btn btn-main' onClick={() => history.goBack()}> <i class="fas fa-arrow-left"></i>&nbsp;&nbsp;&nbsp; Voltar</button>
        </>
    );
};

export default function Detail({match, props}) {
    const [ monster ] = useState(monsters[`${match.params.id - 1}`]);
    document.title = `${monster.name} | Monster Compendium `;
    

    return (
        <div>
           

            <Content container='container'>
                <Row row='justify-content-center'>
                    <Col col='col-8 py-3'>
                        <BackButton></BackButton>
                    </Col>
                </Row>
                <Row row='justify-content-center'>
                    <Col col='col-8'>
                        <div className='detail-bg'>
                            <div style={{backgroundImage:`url(${monster.imgSrc})`, backgroundPosition: 'center top', backgroundRepeat: 'no-repeat', backgroundSize: 'cover'}} className='detail-header'>
                                
                            </div>
                            <div style={{backgroundImage:`url(${texture})`, backgroundPosition: 'center top', backgroundRepeat: 'repeat-x'}} className='p-5'>
                                <h1 className='detail-titles'>{monster.name}</h1>
                                <h3 className='detail-titles'>Nível {monster.D20.monsterLevel}&nbsp;&nbsp;&nbsp; .&nbsp;&nbsp;&nbsp; {monster.type}&nbsp;&nbsp;&nbsp; .&nbsp;&nbsp;&nbsp; {monster.aligment}&nbsp;&nbsp;&nbsp; .&nbsp;&nbsp;&nbsp; ND {monster.cr}&nbsp;&nbsp;&nbsp; .&nbsp;&nbsp;&nbsp; Tamanho {monster.size}</h3>
                                <p className='detail-text py-5'>{monster.desc}</p>
                                <Row>
                                    <Col col='col attributes'>
                                        <p>FOR</p>
                                        <h2>{monster.D20.STR}</h2>
                                    </Col>
                                    <Col col='col attributes'>
                                        <p>DES</p>
                                        <h2>{monster.D20.DEX}</h2>
                                    </Col>
                                    <Col col='col-md attributes'>
                                        <p>RES</p>
                                        <h2>{monster.D20.CON}</h2>
                                    </Col>
                                    <Col col='col attributes'>
                                        <p>INT</p>
                                        <h2>{monster.D20.INT}</h2>
                                    </Col>
                                    <Col col='col attributes'>
                                        <p>SAB</p>
                                        <h2>{monster.D20.WIS}</h2>
                                    </Col>
                                    <Col col='col attributes'>
                                        <p>CAR</p>
                                        <h2>{monster.D20.CHA}</h2>
                                    </Col>
                                </Row>
                                

                            </div>
                        </div>
                    </Col>
                </Row>
            </Content>
        </div>
    )
}
