import './App.css';
import { Content, Row, Col } from './modules/Grid'
import { Card } from './modules/Card'

const monster = [
  {

    id:1,
    name: 'HellHound',
    type: 'Beast',
    size: 'Large',
    cr:'CR 5',
    aligment: 'Chaotic Evil',
    imgSrc: 'https://i.pinimg.com/564x/76/20/91/762091de33f95c5da79d91bc2d6167f4.jpg'

  },{

    id:2,
    name: 'Death Knight',
    type: 'Undead',
    size: 'Medium',
    cr:'CR 10',
    aligment: 'Chaotic Evil',
    imgSrc: 'https://i.pinimg.com/564x/71/3b/29/713b2926ccb2fe9b5550af9f99d57ac0.jpg'

  },{

    id:3,
    name: 'Karasu',
    type: 'Humanoid',
    size: 'Medium',
    cr:'CR 2',
    aligment: 'Neutral Good',
    imgSrc: 'https://i.pinimg.com/564x/09/cb/f8/09cbf8215a8c1eb03d728533003a99d8.jpg'

  }
];




function App() {
  return (
    <div>
      <div style={{height: 20}}></div>

      <Content container='container-fluid'>
        <Row>

          <Col col='col-md-2'>
            <Card 
            key={monster[0].id}
            name={monster[0].name} 
            type={monster[0].type} 
            size={monster[0].size} 
            cr={monster[0].cr} 
            aligment={monster[0].aligment} 
            imgSrc={monster[0].imgSrc} 
            />
          </Col>

          <Col col='col-md-2'>
            <Card 
              key={monster[1].id}
              name={monster[1].name} 
              type={monster[1].type} 
              size={monster[1].size} 
              cr={monster[1].cr} 
              aligment={monster[1].aligment} 
              imgSrc={monster[1].imgSrc} 
              />
          </Col>

          <Col col='col-md-2'>
            <Card 
              key={monster[2].id}
              name={monster[2].name} 
              type={monster[2].type} 
              size={monster[2].size} 
              cr={monster[2].cr} 
              aligment={monster[2].aligment} 
              imgSrc={monster[2].imgSrc} 
              />
          </Col>

          <Col col='col-md-2'>
            <Card 
              key={monster[0].id}
              name={monster[0].name} 
              type={monster[0].type} 
              size={monster[0].size} 
              cr={monster[0].cr} 
              aligment={monster[0].aligment} 
              imgSrc={monster[0].imgSrc} 
              />
          </Col>

          <Col col='col-md-2'>
            <Card 
              key={monster[1].id}
              name={monster[1].name} 
              type={monster[1].type} 
              size={monster[1].size} 
              cr={monster[1].cr} 
              aligment={monster[1].aligment} 
              imgSrc={monster[1].imgSrc} 
              />
          </Col>

          <Col col='col-md-2'>
            <Card 
              key={monster[2].id}
              name={monster[2].name} 
              type={monster[2].type} 
              size={monster[2].size} 
              cr={monster[2].cr} 
              aligment={monster[2].aligment} 
              imgSrc={monster[2].imgSrc} 
              />
          </Col>

        </Row>
      </Content>
    </div>
  );
}

export default App;
