import './App.css';
import { Content, Row, Col } from './modules/Grid'
import { Card } from './modules/Card'

const monster = [
  {

    id:1,
    name: 'HellHound',
    type: 'Beast',
    size: 'Large',
    cr:'CR 5',
    aligment: 'Chaotic Evil',
    imgSrc: 'https://i.pinimg.com/564x/76/20/91/762091de33f95c5da79d91bc2d6167f4.jpg'

  },{

    id:2,
    name: 'Death Knight',
    type: 'Undead',
    size: 'Medium',
    cr:'CR 10',
    aligment: 'Chaotic Evil',
    imgSrc: 'https://i.pinimg.com/564x/71/3b/29/713b2926ccb2fe9b5550af9f99d57ac0.jpg'

  },{

    id:3,
    name: 'Karasu',
    type: 'Humanoid',
    size: 'Medium',
    cr:'CR 2',
    aligment: 'Neutral Good',
    imgSrc: 'https://i.pinimg.com/564x/09/cb/f8/09cbf8215a8c1eb03d728533003a99d8.jpg'

  }
];

function cardRender(monster){
    monster.map(
      () =>
      <Col col='col-md-2'>
        <Card 
        key={monster.id}
        name={monster.name} 
        type={monster.type} 
        size={monster.size} 
        cr={monster.cr} 
        aligment={monster.aligment} 
        imgSrc={monster.imgSrc} 
        /> 
      </Col>
    )
}



function App() {
  return (
    <div>
      <div style={{height: 20}}></div>

      <Content container='container-fluid'>
        <Row>

          {cardRender(monster)}

        </Row>
      </Content>
    </div>
  );
}

export default App;
