import React, { useState } from 'react';
import { Content, Row, Col } from '../modules/Grid';
import monsters from '../monsters.js'

// const cardRender = monsters.map(
//     (monster)=>
//     <Col col='col-md-3'>
//       <Card 
//         id={monster.id}
//         key={monster.id}
//         name={monster.name} 
//         type={monster.type} 
//         size={monster.size} 
//         cr={monster.cr} 
//         aligment={monster.aligment} 
//         imgSrc={monster.imgSrc} 
//       /> 
//     </Col>
//   )

export default function Detail({match}) {
    const [ monster ] = useState(monsters[`${match.params.id - 1}`]);

    return (
        <div>
            <div style={{height: 40}}></div>

            <Content container='container detail-bg'>

                <div className='p-5'>
                    <div>
                        <p>D20</p>
                        <h1>{monster.name}</h1>
                        <p>{monster.aligment}    .    {monster.type}</p>
                    </div>

                    <Content container='container'>
                        <Row>
                            <Col col='col-md-6'>
                                <Row>
                                    <Col col='col'>
                                        <p>STR</p>
                                        <h3>{monster.D20.abilities.STR}</h3>
                                    </Col>
                                </Row>
                            </Col>
                            <Col col='col-md-6'>
                                <p>{monster.desc}</p>
                            </Col>
                        </Row>  
                    </Content>
                </div>

            </Content>
        </div>
    )
}
