import React from 'react'

export function Card(props){
//const imgSrc = 'https://i.pinimg.com/564x/76/20/91/762091de33f95c5da79d91bc2d6167f4.jpg';
// name='' type='' size='' cr='' aligment='' imgSrc=''

    return(
        <div className='pt-3'>
            <div className="text-white border-radius-16 card-height text-left" style={{backgroundImage:`url(${props.imgSrc})`, backgroundPosition: 'center top'}}>
                <div className='bg-dark-card-10 border-radius-16 card-height'>

                    <div className='card-header-custom pt-3'>

                    </div>

                    <div className='card-content p-4'>
                        <p className='monster-category p-0 m-0'>{props.type}  .  {props.size}</p>
                        <h2 className='monster-name'>{props.name}</h2>
                        <p className='monster-quote fst-italic'>{props.cr}  .  {props.aligment}</p>
                    </div>


                </div>
            </div>
        </div>
    )
}
